import React, {Component} from 'react';
import './App.css';

class App extends Component {

  render() {
    const ironLogo = (logoImg) => {
      return logoImg='./images/ironhack-logo.svg';
    }
    const navMenu = (navImg) => {
      return navImg= './images/menu-top.svg';
    }
    const decBox = (declarativeImg) => {
      return declarativeImg='./images/icon1.png';
    }
    const comBox = (componentsImg) => {
      return componentsImg='./images/icon2.png';
    }
    const singBox = (singleImg) => {
      return singleImg='./images/icon3.png';
    }
    const jsxBox = (jsxImg) => {
      return jsxImg='./images/icon4.png';
    }
    const logoReact = (reactImg) => {
      return reactImg='./images/react-logo.svg';
    }
    const btnReact = (awesomeBtn) => {
      return awesomeBtn='Awesome';
    }


    const formatDescription = (subtitle) => {
      return `${subtitle.description}`;
    }

    const subtitle = {
      description: 'You will learn a Frontend framework from scratch, to become an Ninka Developer'
    };

    const element = (
      <p className = "subtitle">
      {formatDescription(subtitle)}!
      </p>
    );
    const awesome = (
      <button className = "button">
      {btnReact(awesomeBtn)}!
      </button>
    );
    const image = (
      <img src={ironLogo(logoImg)}/>
    )
    const nav = (
      <img className="nav" src={navMenu(navImg)}/>
    )

    //PARA REFELEJAR IMAGES BOXES X SECCIONES
    const decSection = (
      <img src={decBox(declarativeImg)}/>
    )
    const comSection = (
      <img src={comBox(componentsImg)}/>
    )
    const singSection = (
      <img src={singBox(singleImg)}/>
    )
    const jsxSection = (
      <img src={jsxBox(jsxImg)}/>
    )
    const reactLogoImg = (
      <img className="imageReact" src={logoReact(reactImg)}/>
    )

    //SE AÑADIRÁ A ROOT
    return (
      <div>
      <div className="App">
        {reactLogoImg}
        {nav}
      {image}
        <h1 className ="title"> Say hello to ReactJs! </h1>
        {element}
        {awesome}
        </div>
        <div className="allSections">
        <div className="Sections">
          {decSection}
          <h3>Declarative</h3>
          <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit.</p>
        </div>
        <div className="Sections">
          {comSection}
          <h3>Components</h3>
          <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit.</p>
        </div>
        <div className="Sections">
          {singSection}
          <h3>Single-Way</h3>
          <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit.</p>
        </div>
        <div className="Sections">
          {jsxSection}
          <h3>JSX</h3>
          <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit.</p>
        </div>
        </div>
      </div>
    );
  }
}

export default App;
